import{FacebookAuthProvider, getAuth, GithubAuthProvider, GoogleAuthProvider, signInWithPopup} from 'firebase/auth'
import React, { useState } from 'react';
import './login.css'
import FacebookIcon from '@mui/icons-material/Facebook';
import GitHubIcon from '@mui/icons-material/GitHub';
import GoogleIcon from '@mui/icons-material/Google';
import InitializeAuthentication from '../../Firebase/Firebase-initialize';
InitializeAuthentication()
const Googleprovider = new GoogleAuthProvider() 
const Githubprovider = new GithubAuthProvider()
const FacebookProvider = new FacebookAuthProvider()
const Auth = getAuth()
const Login = () => {
    const [user, setUser] = useState({})
    const handleGithhubSignIn =()=>{
        signInWithPopup (Auth, Githubprovider)
        .then(result=>{
            const {displayName,email,photoURL}=result.user
            const GithubUser ={
                name: displayName,
                photo: photoURL,
                email: email
            }
            setUser(GithubUser)
            console.log(user)
        })
    

    }
    const handleFacebookSignIn =()=>{
        signInWithPopup(Auth,FacebookProvider).then(result=>{const {displayName,email,photoURL} = result.user; const fbuser={name: displayName, email: email, photo: photoURL } ; setUser(fbuser)})
    }
    const handleGoogleSignIn=()=>{
       
        signInWithPopup(Auth,Googleprovider)
        .then(result=>{
            const loginuser = result.user
            const {displayName, email, photoURL} = loginuser
           const  loginuserArray ={
               name: displayName,
               email: email,
               photo: photoURL


            }
            setUser(loginuserArray)

            
        })
        .catch(error=>{

        })
   
    }
  
    return (
        
        <div class="containerr">
        {
            user.email && <div>
            <h2> wellcome {user.name}</h2>
            
            </div>
        }
    
        <div class="screen">
        
            <div class="screen__content">
                <form class="login">
                    <div class="login__field">
                        <i class="login__icon fas fa-user"></i>
                        <input type="text" class="login__input" placeholder="User name / Email"/>
                    </div>
                    <div class="login__field">
                        <i class="login__icon fas fa-lock"></i>
                        <input type="password" class="login__input" placeholder="Password"/>
                    </div>
                    <button class="button login__submit">
                        <span class="button__text">Log In Now</span>
                        <i class="button__icon fas fa-chevron-right"></i>
                    </button>				
                </form>
                <div class="social-login">
                    <h3>log in via</h3>
                    <div class="social-icons">
                        <a href="#" class="social-login__icon fab fa-instagram" onClick={handleGithhubSignIn} ><GitHubIcon/> </a>
                        <a href="#" class="social-login__icon fab fa-facebook " onClick={handleFacebookSignIn} ><FacebookIcon/> </a>
                        <a href="#" class="social-login__icon fab fa-twitter" onClick={handleGoogleSignIn}><GoogleIcon/> </a>
                    
                    </div>
                </div>
            </div>
            <div class="screen__background">
                <span class="screen__background__shape screen__background__shape4"></span>
                <span class="screen__background__shape screen__background__shape3"></span>		
                <span class="screen__background__shape screen__background__shape2"></span>
                <span class="screen__background__shape screen__background__shape1"></span>
            </div>		
        </div>
    </div>
    );
};

export default  Login ;
