import React, { useState } from 'react';
import { Sliderpicture } from './SliderData';
import './ImgageSlider.css'
import ArrowCircleLeftTwoToneIcon from '@mui/icons-material/ArrowCircleLeftTwoTone';
import ArrowCircleRightTwoToneIcon from '@mui/icons-material/ArrowCircleRightTwoTone';
import { sliderClasses } from '@mui/material';

const ImgageSlider = ({slider}) => {
    const [current,setCurrent]= useState(0)
    const length = slider.length
    const nextSlide= ()=>{
        setCurrent(current===length - 1 ? 0: current+1)
        console.log(current)
    }
    const prevSlide= ()=>{
        setCurrent(current===0 ? length-1 :current-1)
      

    }
    if(!Array.isArray(slider)|| slider.length<=0){
        return null;
    }

    return (
        <section className='slider-section'>
        <ArrowCircleLeftTwoToneIcon className='left-arrow' onClick={prevSlide} />
        <ArrowCircleRightTwoToneIcon className='Right-arrow' onClick={nextSlide} />
            {Sliderpicture.map((slider, index)=>{
                return(
                    <div className={index ===current?'slide-active':'slide' } key={index}>

                     <img src={slider.image} alt="travel image" className='Carousel-image' />
                     
                     </div>
                   
                )
            })}
        </section>
    );
};

export default ImgageSlider;
